package com.afsal.dev.nasaapp.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.AsyncListDifferimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.nasaapp.databinding.ListItemBindingimport com.afsal.dev.nasaapp.models.Planetery_dataItemclass ListingAdapter(private val ItemSelected: (item: Planetery_dataItem) -> Unit) :    RecyclerView.Adapter<ListingAdapter.ItemHolder>() {    private val diffCallBack = object : DiffUtil.ItemCallback<Planetery_dataItem>() {        override fun areItemsTheSame(            oldItem: Planetery_dataItem,            newItem: Planetery_dataItem        ): Boolean {            return oldItem.title == newItem.title        }        override fun areContentsTheSame(            oldItem: Planetery_dataItem,            newItem: Planetery_dataItem        ): Boolean {            return oldItem == newItem        }    }    val differ = AsyncListDiffer(this, diffCallBack)    inner class ItemHolder(val binding: ListItemBinding) : RecyclerView.ViewHolder(binding.root) {        init {            binding.container.setOnClickListener {                val position = adapterPosition                if (position != RecyclerView.NO_POSITION) {                    val item = differ.currentList[position]                    ItemSelected(item)                }            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {        return ItemHolder(            ListItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {        val item = differ.currentList[position]        holder.binding.textView.text = item.title        holder.binding.textView2.text = item.date    }    override fun getItemCount() = differ.currentList.size}