package com.afsal.dev.nasaapp.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.AsyncListDifferimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.nasaapp.databinding.ListItemBindingimport com.afsal.dev.nasaapp.models.PlaneteryDataItemclass ListingAdapter(private val ItemSelected: (item: PlaneteryDataItem) -> Unit) :    RecyclerView.Adapter<ListingAdapter.ItemHolder>() {    private val diffCallBack = object : DiffUtil.ItemCallback<PlaneteryDataItem>() {        override fun areItemsTheSame(            oldItem: PlaneteryDataItem,            newItem: PlaneteryDataItem        ): Boolean {            return oldItem.title == newItem.title        }        override fun areContentsTheSame(            oldItem: PlaneteryDataItem,            newItem: PlaneteryDataItem        ): Boolean {            return oldItem == newItem        }    }    val differ = AsyncListDiffer(this, diffCallBack)    inner class ItemHolder(val binding: ListItemBinding) : RecyclerView.ViewHolder(binding.root) {        init {            binding.container.setOnClickListener {                val position = adapterPosition                if (position != RecyclerView.NO_POSITION) {                    val item = differ.currentList[position]                    ItemSelected(item)                }            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {        return ItemHolder(            ListItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {        val item = differ.currentList[position]        holder.binding.textView.text = item.title        holder.binding.textView2.text = item.date        //  val date=   DateFormat.getDateInstance(DateFormat.MEDIUM).format(item.date.toLong())    }    override fun getItemCount() = differ.currentList.size}