package com.afsal.dev.nasaapp.databaseimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Queryimport com.afsal.dev.nasaapp.models.PlaneteryDataItemimport kotlinx.coroutines.flow.Flow@Daointerface PlaneteryDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertPlaneteryData(planeteryData:List<PlaneteryDataItem>)    @Query("DELETE FROM planetery_table ")    suspend fun deleteAllPlaneteryData()    @Query("SELECT * FROM planetery_table")     fun getAllPlaneteryData(): Flow<List<PlaneteryDataItem>>}