package com.afsal.dev.nasaapp.diimport android.app.Applicationimport androidx.room.Roomimport com.afsal.dev.nasaapp.database.PlanteryDatabaseimport com.afsal.dev.nasaapp.helper.Constants.BASE_URLimport com.afsal.dev.nasaapp.network.ApiServiceimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)object AppModule {    @Singleton    @Provides    fun provideRetrofitInstance(): Retrofit =        Retrofit.Builder()            .baseUrl(BASE_URL)            .addConverterFactory(GsonConverterFactory.create())            .build()    @Singleton    @Provides    fun provideApiService(retrofit: Retrofit):ApiService=        retrofit.create(ApiService::class.java)    @Singleton    @Provides    fun providesDatabase(app:Application):PlanteryDatabase =        Room.databaseBuilder(app,PlanteryDatabase::class.java,"planetery_database")            .build()}