package com.afsal.dev.nasaapp.helpersealed class Resource<T>(    val data: T? = null,    val error: Throwable? = null) {    class Success<T>(data: T) : Resource<T>(data)    class Loading<T>(data: T? = null) : Resource<T>(data)    class Error<T>(throwable: Throwable, data: T? = null) : Resource<T>(data, throwable)}///https://www.section.io/engineering-education/firebase-offline-caching///https://proandroiddev.com/enabling-cache-offline-support-on-android-using-room-4b82ae0c9c88